import React, { useState } from 'react';
import { View, ScrollView, StyleSheet, Modal, Text, TouchableOpacity } from 'react-native';
import Cardlivro from '../../Cards/Cardlivro';

export default function LibraryCatalog({ navigation }) {
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedLivro, setSelectedLivro] = useState(null);

  const handleCardPress = (livro) => {
    setSelectedLivro(livro);
    setModalVisible(true);
  };

  const handleBorrowPress = () => {
    // Lógica de empréstimo aqui
    console.log('Livro emprestado:', selectedLivro.titulo);
    setModalVisible(false);
  };

  return (
    <ScrollView>
      {/* Renderizar os cards de livros */}
      {Livros.map((Livro) => (
        <TouchableOpacity key={Livro.id} onPress={() => handleCardPress(Livro)}>
          <Cardlivro livro={Livro} />
        </TouchableOpacity>
      ))}

      {/* Modal de Empréstimo */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalContainer}>
          {selectedLivro && (
            <View style={styles.modalContent}>
              <Cardlivro livro={selectedLivro} isModalCard={true} />
              <TouchableOpacity
                style={styles.borrowButton}
                onPress={handleBorrowPress}
              >
                <Text style={styles.borrowButtonText}>Emprestar</Text>
              </TouchableOpacity>
            </View>
          )}
        </View>
      </Modal>
    </ScrollView>
  );
}


