import React, { useState, useEffect } from 'react';
import { View, Text, ScrollView, StyleSheet, Modal } from 'react-native';
import livroService from '../../services/Livro';
import Cardlivro from '../../Cards/Cardlivro';

export default function LibraryCatalog({ navigation }) {
  const [Livros, setLivros] = useState([]);
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedLivro, setSelectedLivro] = useState(null);

  async function BuscarDados2() {
    const data = await livroService.getAllLivros();
    setLivros(data);
  }

  useEffect(() => {
    BuscarDados2();
  }, []);

  const handleCardPress = (livro) => {
    setSelectedLivro(livro);
    setModalVisible(true);
  };

  return (
    <ScrollView>
      {Livros.map((Livro) => (
        <Cardlivro
          key={Livro.id}
          livro={Livro}
          onPress={() => handleCardPress(Livro)}
        />
      ))}

      {/* Modal de Empréstimo */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            {selectedLivro && (
              <Cardlivro livro={selectedLivro} isModalCard={true} />
            )}
            <View style={styles.modalButtons}>
              <TouchableOpacity
                style={styles.borrowButton}
                onPress={() => {
                  // Lógica de empréstimo aqui
                  console.log('Livro emprestado:', selectedLivro.titulo);
                  setModalVisible(false);
                }}
              >
                <Text style={styles.borrowButtonText}>Emprestar</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.closeButton}
                onPress={() => setModalVisible(false)}
              >
                <Text style={styles.closeButtonText}>Fechar</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
    </ScrollView>
  );
}





