import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';

const LivroCard = ({ livro }) => {
  return (
    <View style={styles.cardContainer}>
      <Image source={{ uri: livro?.imagem }} style={styles.cardImagem} />
      <View style={styles.cardInfo}>
        <Text style={styles.cardTexto}>{livro?.livro}</Text>
        <Text style={styles.cardTexto}>Autor: {livro?.autor}</Text>
        <Text style={styles.cardTexto}>Categoria: {livro?.categoria}</Text>
        <Text style={styles.cardTexto}>Editora: {livro?.editora}</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  cardContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
    backgroundColor: '#fff', // Fundo com transparÃªncia
    borderRadius: 10,
    padding: 10,
  },
  cardImagem: {
    width: 100,
    height: 150,
    resizeMode: 'cover',
    marginRight: 10,
  },
  cardInfo: {
    flex: 1,
  },
  cardTexto: {
    fontSize: 16,
  },
});

export default LivroCard;
