import React from "react";
import { View, Image, StyleSheet, Text } from "react-native";

function Card(props) {
  return (
    <View style={styles.cardContainer}>
      <Image
        style={styles.cardImagem}
        source={{ uri: props.livro.capa.url }}
      />
      <View style={styles.cardInfo}>
        <Text style={styles.cardTexto}>{props.livro.titulo}</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  cardContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 10,
  },
  cardImagem: {
    width: 100,
    height: 150,
    borderRadius: 10, // Adicionei bordas para manter o estilo
    resizeMode: 'cover',
    marginRight: 10,
  },
  cardInfo: {
    flex: 1,
  },
  cardTexto: {
    fontSize: 16,
  },
});

export default Card;
