import React from "react";
import { View, Image, StyleSheet, Text } from "react-native";

function Card(props) {
  const { livro } = props; // Desestruturando as propriedades para facilitar o acesso

  return (
    <View style={styles.cardContainer}>
      <Image
        style={styles.cardImagem}
        source={{ uri: livro.capa.url }}
      />
      <View style={styles.cardInfo}>
        <Text style={styles.cardTitulo}>{livro.titulo}</Text>
        <Text style={styles.cardAutor}>{livro.autor}</Text>
        <Text style={styles.cardISBN}>ISBN: {livro.isbn}</Text>
        <Text style={styles.cardAnoPublicacao}>Ano:2011</Text>
        <Text style={styles.cardAnoPublicacao}>Autor:2011</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  cardContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 10,
  },
  cardImagem: {
    width: 100,
    height: 150,
    borderRadius: 10,
    resizeMode: 'cover',
    marginRight: 10,
  },
  cardInfo: {
    flex: 1,
  },
  cardTitulo: {
    fontSize: 16,
    marginBottom: 5,
  },
  cardAutor: {
    fontSize: 14,
    color: '#555',
    marginBottom: 5,
  },
  cardISBN: {
    fontSize: 14,
    color: '#555',
    marginBottom: 5,
  },
  cardAnoPublicacao: {
    fontSize: 14,
    color: '#555',
  },
});

export default Card;
